use StudentsWebApi;CREATE TABLE Students (    id INT IDENTITY(1,1) PRIMARY KEY,    fname NVARCHAR(30) NOT NULL,	lname NVARCHAR(30) NOT NULL,	age int NOT NULL CONSTRAINT CK_age CHECK (age > 0 and age < 120),    email NVARCHAR(30) NOT NULL UNIQUE,    password NVARCHAR(MAX) NOT NULL,    gender VARCHAR(20) CONSTRAINT CK_gender CHECK (gender IN ('Male', 'Female')) NOT NULL,);INSERT INTO Students Values('Haresh', 'Prajapati', 21, 'haresh@gmail.com', '123456', 'Male');INSERT INTO Students Values('Manav', 'Patel', 22, 'manav@gmail.com', '123456', 'Male');INSERT INTO Students Values('Mohit', 'Chauhan', 23, 'mohith@gmail.com', '123456', 'Male');INSERT INTO Students Values('Vishnu', 'Prajapati', 22, 'vishnu@gmail.com', '123456', 'Male');select * from students;